exports.AddImage = function(req, res) {
  console.log("Image: " + req.params.id);
  req.pipe(req.busboy);
  req.busboy.on('file', function(fieldname, file, filename, id) {
    var fstream, name;
    console.log("Uploading: " + filename);
    fstream = fs.createWriteStream('./public/images/' + filename);
    file.pipe(fstream);
    name = filename;
    console.log(name);
    fstream.on('close', function() {
      easyimg.resize({
        src: './public/images/' + filename,
        dst: './public/images/gallerySize/' + filename,
        width: 400,
        height: 280
      }).then(function(image) {
        return console.log('Resized: ' + image.width + ' x ' + image.height);
      }, function(err) {
        return console.log(err);
      });
      easyimg.rescrop({
        src: './public/images/' + filename,
        dst: './public/images/thumbs/' + filename,
        width: 100,
        height: 100,
        gravity: "Center"
      }).then(function(image) {
        return console.log('Resized and cropped: ' + image.width + ' x ' + image.height);
      }, function(err) {
        return console.log(err);
      });
      Product.findByIdAndUpdate(req.params.id, {
        $push: {
          images: filename
        }
      }, {
        safe: true,
        upsert: true
      }, function(err, product) {
        if (err) {
          res.send(err);
        }
        Product.find(function(err, products) {
          if (err) {
            res.send(err);
          }
          res.json(products);
        });
      });
    });
    return name;
  });
};

